sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // Add your code here

    email.addAddress('cc', current.u_employee.manager.email + '');

    var sameBirthday = new GlideRecord('x_58872_needit_occasions');
    sameBirthday.addQuery('occasion_month', current.occasion_month);
    sameBirthday.addQuery('occasion_ady', current.occasion_day);
    sameBirthday.addQuery('u_ocassion', 'birthday');

    sameBirthday.query();

    template.print(sameBirthday.getRowCount() - 1 + 'empregados na empresa que compartilham seu aniversÃ¡rio');

})(current, template, email, email_action, event);
